import React, { useState } from "react";
import "./App.css";
import searchIcon from "./images/search.png";
import weatherIcon from "./images/cloud.png"; // Use your actual weather icon here

function App() {
  const [city, setCity] = useState("");
  const [weatherData, setWeatherData] = useState(null);

  const fetchWeather = async () => {
    if (!city) return;
    const apiKey = "YOUR_API_KEY"; // Replace with your OpenWeatherMap API key
    const url = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;

    try {
      const response = await fetch(url);
      const data = await response.json();

      if (data.cod === 200) {
        setWeatherData(data);
      } else {
        setWeatherData(null);
        alert("City not found.");
      }
    } catch (error) {
      alert("Error fetching weather data.");
      console.error(error);
    }
  };

  return (
    <div className="app-container">
      <h1>Weather App</h1>
      <div className="search-box">
        <input
          type="text"
          placeholder="Search any city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && fetchWeather()}
        />
        <img src={searchIcon} alt="search" onClick={fetchWeather} />
      </div>

      {weatherData && (
        <div className="weather-info">
          <h2>{weatherData.name}, {weatherData.sys.country}</h2>
          <h3>{weatherData.weather[0].main}</h3>
          <p>{Math.round(weatherData.main.temp)}Â°C</p>
          <img className="weather-icon" src={weatherIcon} alt="weather" />
        </div>
      )}
    </div>
  );
}

export default App;
